async function MERGE_IMPORTED_PRODUCT(list_product) {
    let imported = await action.GET_LIST_IMPORTED()
    let str = []
    if (imported.data.status) {
        str = imported.data.data.imported
    }
    return list_product.map(function (el,idx) {
        console.log(el);
        if (str.indexOf(el.id) >= 0) {
            el.isImported = true
        } else {
            el.isImported = false
        }
        return el
    })
}

let action =  {
    GET_LIST_COLLECTIONS({commit}, { }){
        return new Promise((resolve , reject) =>{
            axios.get('/api/collections' ).then((res)=>{
                let {  status , data } = res.data;
                if( status ){
                    commit('UPDATE_COLLECTION' , data);
                }
                resolve(res.data);
            }, (error) =>{
                reject(error)
            })
        })
    },
    GET_LIST_COUNTRIES({commit}, { }){
        return new Promise((resolve , reject) =>{
            axios.get('/api/supplier/country' ).then((res)=>{
                let {  status , data } = res.data;
                if( status ){
                    commit('UPDATE_COUNTRY' , data);
                }
                resolve(res.data);
            }, (error) =>{
                reject(error)
            })
        })
    },
    GET_LIST_SUPPLIERS({commit}, { }){
        return new Promise((resolve , reject) =>{
            axios.get('/api/suppliers' ).then((res)=>{
                let {  status , data } = res.data;
                if( status ){
                    commit('UPDATE_SUPPLIER' , data);
                }
                resolve(res.data);
            }, (error) =>{
                reject(error)
            })
        })
    },
    GET_LIST_BANNERS({commit} , {}){
        return new Promise((resolve , reject) =>{
            setTimeout(function () {
                commit('UPDATE_BANNERS' , [
                    {
                        id : '123',
                        url : 'adsadad',
                        image : 'https://dropsquare-api-dev.fireapps.io/uploads/images/1627401458103778/V9NJQmix7sS5KfJ8EY1eYv4FtwtnHMH6IZs341OM.png'
                    },
                    {
                        id : '345',
                        url : 'adsadad',
                        image : 'https://dropsquare-api-dev.fireapps.io/uploads/images/1627401458103778/IeXdcwKsmODuK3P2phf0sZQ27sEw7zHVZTCRmy6r.jpeg'
                    },
                    {
                        id : '567',
                        url : 'adsadad',
                        image : 'https://dropsquare-api-dev.fireapps.io/uploads/images/1627401458103778/3aLH16PJVAGumjjCpcVLss1XlLuBrLbJGJZwD2Cu.jpeg'
                    }
                ])
                resolve()
            }, 500);
        })
    },
    GET_PRODUCT_QUESTION({commit} , { id , current_page = 1, limit = 5 }){
        var params = {
            current_page : current_page ,
            limit : limit
        }
        return new Promise((resolve , reject) =>{
            axios.get(`api/seller/products/${id}/questions` , { params : params} ).then((res)=>{
                resolve(res.data)
            }, (error) =>{
                reject(error)
            })
        })
    },
    POST_PRODUCT_QUESTION({} , { id , content}){
        return new Promise((resolve , reject) =>{
            axios.post(`api/seller/products/${id}/questions/add` , { title : content } ).then((res)=>{
                resolve(res.data)
            }, (error) =>{
                reject(error)
            })
        })
    },
    async GET_LIST_IMPORTED() {
      return await axios.get(`/api/seller/products/imported`);
    },
    async GET_RECENT_PRODUCT({commit} , ids ){
        return new Promise((resolve , reject) =>{
            axios.get('/api/seller/products/recent_viewed', {
                params : {
                    ids : ids
                }
            }).then(async (res)=>{
                if (res.data.status) {
                    res.data.data = await MERGE_IMPORTED_PRODUCT(res.data.data)
                }
                resolve(res)
            }, (error) =>{
                reject(error)
            })
        })
    },
    GET_LIST_BEST_SELLING({commit} , product_id){
        return new Promise((resolve , reject) =>{
            axios.get(`/api/seller/products/${product_id}/best_selling` ).then(async (res)=>{
                if (res.data.status) {
                    res.data.data = await MERGE_IMPORTED_PRODUCT(res.data.data)
                }
                resolve(res.data)
            }, (error) =>{
                reject(error)
            })
        })
    },
    GET_TOP_CATEGORY({commit}){
        return new Promise((resolve , reject) =>{
            axios.get('/api/seller/favorite_collections' ).then(async (res)=>{
                resolve(res.data)
            }, (error) =>{
                reject(error)
            })
        })
    },
    GET_PRODUCT_DETAIL({ } , { id }){
        return new Promise((resolve , reject) =>{
            axios.get(`/api/seller/products/${id}` ).then(async (res)=>{
                if (res.data.status) {
                    res.data.data = (await MERGE_IMPORTED_PRODUCT([res.data.data]))[0]
                }
                resolve(res.data)
            }, (error) =>{
                reject(error)
            })
        })
    },
    async GET_LIST_PRODUCTS({commit} , { keyword = '' , is_premium = 0 , limit = 12 , page = 1 , supplier_id = null , collection_id = null ,  prices = null , shipping_from = null , shipping_to = null , sort = 'lasted'}){
        var params = {
            keyword : keyword,
            limit : limit ,
            current_page : page ,
            is_premium : is_premium
        }
        if( sort ){
            params['sort'] = sort
        }
        if( collection_id ){
            params['collection_id'] = collection_id
        }
        if( prices ){
            params['prices'] = prices
        }
        if( supplier_id ){
            params['supplier_id'] = supplier_id
        }
        if( shipping_from ){
            params['shipping_from'] = shipping_from
        }
        if( shipping_from ){
            params['shipping_to'] = shipping_to
        }

        return await new Promise((resolve , reject) =>{
            axios.get('/api/seller/products', { params : params} ).then(async (res)=>{
                if (res.data.status) {
                    // console.log(res.data.data);
                    res.data.data = await MERGE_IMPORTED_PRODUCT(res.data.data)
                }
                resolve(res.data)
            }, (error) =>{
                reject(error)
            })
        })
    },
    async IMPORT_PRODUCT({commit} , import_info ){
        return await axios.post('/api/seller/products/import', import_info)
    },

    async REMOVE_IMPORTED_PRODUCT({commit} , import_info ){
        return await axios.post('/api/seller/products/remove_imported', import_info)
    }
}

export default action
