export default {
    UPDATE_COLLECTION(state , data){
        state.collections = data ? data : []
    },
    UPDATE_COUNTRY(state , data){
        state.countries = data ? data : []
    },
    UPDATE_SUPPLIER(state , data){
        state.suppliers = data ? data : []
    },
    UPDATE_SIDEBAR_OPTION(state , { collection_id , more }){
        if( collection_id ){
            state.sidebar.collection_id = collection_id
        }
        if( more ){
            state.sidebar.more = more
        }
    },
    UPDATE_BANNERS(state , data){
        state.banners = data ? data : []
    },
}
