result:
@import 'src/assets/style';

$border-color: #eee;
.product-detail-container {
    &-loading {
        min-height: 500px;
        display: flex;
        justify-content: center;
        align-items: center;
        .icon-loading {
            height: 60px;
            width: 60px;
            background: $yellow;
        }
    }
    .overflow-text {
        display: block;
        display: -webkit-box;
        -webkit-box-orient: vertical;
        overflow: hidden;
        text-overflow: ellipsis;
    }
    .product-detail-sticky-bar {
        opacity: 0;
        background: white;
        transition: all 0s;
        border-bottom: solid 1px lightgray;
        box-shadow: 0px 1px 7px 0px rgba(0, 0, 0, 0.2);
        .detail-sticky {
            display: flex;
            justify-content: space-between;
            flex-flow: row wrap;
            height: 60px;
            .sticky-bar-item {
                line-height: 60px;
                display: flex;
                .product-name {
                    font-size: 22px;
                    max-width: 450px;
                    /*flex: 1;*/
                    @extend .overflow-text;
                    -webkit-line-clamp: 1;

                }
                .supplier-name {
                    font-size: $default-font-size;
                }
                .product-price {
                    font-weight: bold;
                    font-size: 22px;
                }
                .product-old-price {
                    color: lightgray;
                    font-size: $default-font-size;
                    text-decoration: line-through;
                }
                .product-old-price, .product-price {
                    vertical-align: middle;
                }
                .import-actions {
                    .btn-importing {
                        .icon-load {
                            display: none;
                        }
                        .circle-loader {
                            display: inline-block;
                        }
                    }
                }
            }
        }
    }
    .detail-description {
        div.layout-item:nth-child(1) {
            padding-right: 10px;
        }
        div.layout-item:nth-child(2) {
            padding-left: 10px;
        }
        .description-info {
            padding: 40px 40px 20px;
        }
        .best-selling {
            padding: 40px 30px 30px;
        }
        .best-selling {
            .list-product {
                padding: 0px;
                .product {
                    &-item {
                        display: flex;
                        flex-direction: row;
                        margin-bottom: 15px;
                        width: 100%;
                        overflow: hidden;
                        a {
                            text-decoration: none;
                        }
                        &:last-child {
                            margin-bottom: 0;
                        }
                    }
                    &-image {
                        width: 60px;
                        height: 60px;
                        flex: 60px auto;
                        border-radius: 3px;
                        overflow: hidden;
                        display: flex;
                        justify-content: center;
                        align-items: center;
                        img {
                            max-width: 100%;
                            max-height: 100%;
                        }
                    }
                    &-content {
                        flex: 1 1;
                        padding-left: 5px;
                        overflow: hidden;
                    }
                    &-title {
                        color: $text-color;
                        font-size: 13px;
                        display: inline-block;
                        font-weight: bold;
                        &:hover {
                            color: $yellow;
                        }
                    }
                    &-supplier-and-price {
                        display: flex;
                        flex-direction: row;
                        flex-wrap: wrap;
                    }
                    &-supplier {
                        color: $text-color;
                        flex: 1 1;
                        a {
                            color: $yellow;
                            margin-left: 5px;
                        }
                    }
                    &-price {
                        font-size: 17px;
                        font-weight: bold;
                        text-align: right;
                        flex: 1 1;
                    }
                }
            }
        }
    }
}

.sticky {
    position: fixed;
    width: 100%;
    left: 0;
    top: 0;
    z-index: 100;
    opacity: 1 !important;
    transition: all .4s !important;
}

.app-product-detail {

}

.product {
    &-breadcrumb {
        padding: 15px 0;
        background: #f2f2f2;
    }
    &-head {
        background: #fff;
        padding: 20px 0;
    }
    &-body {
        background: #F2F2F2;
        padding: 30px 0;
    }
    &-foot {
        background: #fff;
        padding: 30px 0;
    }
    &-image {
        position: relative;
        &-box {
            border: 1px solid $border-color;
            display: flex;
            justify-content: center;
        }
        &-carousel {
            width: 100%;
            height: 100px;
            overflow: hidden;
            padding: 15px 0px;

            .item-slide {
                width: 70px;
                height: 70px;
                border: 2px solid $border-color;
                display: flex;
                justify-content: center;
                align-items: center;
                transition: all 0.3s ease;
                img {
                    max-width: 100%;
                    max-height: 100%;
                }
                &.is-active {
                    border-color: $yellow;
                }
            }
        }
    }
    &-title {
        margin-bottom: 5px;
        h1 {
            font-size: 25px;
            font-weight: 500;
            margin: 0;
        }
    }
    &-supplier {
        span, strong {
            display: inline-block;
        }
        strong {
            color: $yellow;
            margin-left: 5px;
        }
    }
    &-properties {
        .item-property {
            margin-bottom: 20px;
            &-title {
                font-size: 16px;
                font-weight: 500;
                margin-bottom: 5px;
                line-height: 16px;
                text-transform: uppercase;
            }
            &-value {
                display: flex;
                flex-direction: row;
                flex-wrap: wrap;
                margin: 0 -7px;
                .item-value {
                    padding: 0 7px;
                    margin: 0 0 7px;
                    .btn {
                        &:focus {
                            outline: none;
                            box-shadow: none;
                        }
                    }
                    &.is-active {
                        .item-value {
                            &-image {
                                border-color: $yellow;
                            }
                            &-text {
                                border-color: $yellow;
                            }
                        }
                    }
                    &:first-child {
                        margin-left: 0;
                    }
                    &-image {
                        padding: 0;
                        height: 50px;
                        width: 50px;
                        min-width: 50px;
                        border-radius: 8px;
                        border: 2px solid #ddd;
                        overflow: hidden;
                        img {
                            max-width: 100%;
                            max-height: 100%;
                        }
                    }
                    &-text {
                        min-height: 28px;
                        padding: 0 7px;
                        min-width: 60px;
                        overflow: hidden;
                        border: 1px solid #ddd;
                        border-radius: 19px;
                    }
                }
            }
        }
    }
    &-price {
        .item-price {
            &-title {
                font-size: 16px;
                font-weight: bold;
                margin-bottom: 5px;
                line-height: 16px;
                text-transform: uppercase;
            }
            &-value {
                font-size: 40px;
                font-weight: 500;
                margin-bottom: 5px;
                line-height: 40px;
            }
        }
    }
    &-actions {
        margin-top: 30px;
        display: flex;
        flex-wrap: nowrap;
        flex-direction: row;
        .btn {
            padding: 0 25px;
            border-radius: 25px;
            height: 42px;
            line-height: 42px;
            align-items: center;
            justify-content: center;
            display: inline-flex;
            // flex: 1 1;
            margin: 0 5px 5px 5px;
            font-size: 14px;
            font-weight: 500;
            &.btn-importing {
                .icon-load {
                    display: none;
                }
                .circle-loader {
                    display: inline-block;
                }
            }
            &-import-list {
                color: #fff;
                background: $yellow;
            }
            &-view-detail {
                border: 1px solid #ccc;
            }
        }
    }
    &-return-policy {
        display: flex;
        flex-wrap: nowrap;
        flex-direction: row;
        .return-policy {
            &-title {
                font-size: 14px;
                text-transform: uppercase;
                white-space: nowrap;
                font-weight: 500;
            }
            &-value {
                padding-left: 15px;
                strong {
                    font-size: 14px;
                    font-weight: 500;
                }
                p {
                    font-size: 12px;
                }
            }
        }
    }
    &-shipping {
        .item-shipping {
            display: flex;
            flex-wrap: nowrap;
            flex-direction: row;
            margin-bottom: 10px;
            .item-title {
                font-weight: bold;
                width: 160px;
            }
            .item-value {
                width: calc(100% - 160px);
                font-weight: bold;
                a {
                    color: $yellow;
                    &:hover {
                        color: $yellow;
                    }
                }
            }
            &-methods {
                .item-title {
                    width: 100%;
                    font-weight: bold;
                    margin-bottom: 10px;
                }
                .list-shipping-country {
                    border: 1px solid #eee;
                    .item-warehouse {
                        &-name {
                            font-size: 14px;
                            text-transform: uppercase;
                            padding: 10px;
                            font-weight: bold;
                            text-decoration: underline;
                            color: $yellow;
                            background: #f2f2f2;
                            border-bottom: 1px solid #eee;
                        }
                        &-shipping {
                            display: flex;
                            flex-direction: row;
                            flex-wrap: nowrap;
                            padding: 10px 10px 10px 15px;
                            border-bottom: 1px solid #eee;
                            &-head {
                                font-weight: bold;
                                margin: 0;
                            }
                            .item-shipping-cost, .item-shipping-fee, .item-shipping-from, .item-shipping-to {
                                width: 20%;
                                overflow: hidden;
                            }
                            .item-shipping-cost, .item-shipping-fee, .item-shipping-from, .item-shipping-to {
                                text-align: center;
                            }
                            .item-country {
                                width: 20%;
                            }
                        }
                        &-except {
                            padding: 10px;
                            text-align: center;
                        }
                    }
                }
            }
        }
    }
}
