    import ProductEmpty from '../partials/product-empty'
    import ProductLoading from '../partials/product-loading'
    import ListProducts from '../partials/list-products'
    import AppSidebar from '../partials/sidebar'
    import TabFilterRouter from '../partials/tab-filter-router'
    import { mapGetters } from 'vuex';
    import helper from '@/plugins/helper'
    import Pagination  from '@/components/pagination'
	export default{
        name : 'Premiums',
        components: {
            AppSidebar , TabFilterRouter ,ListProducts , Pagination , ProductEmpty , ProductLoading
        },
        metaInfo() {
            return {
                title: this.meta.title,
            }
        },
        data(){
            return {
                isLoading: false,
                sortables : ['last' ,'trending' ,'price'],
                collection_id : '',
                products : [],
                default_sort : 'last',
                meta:{
                    title : 'All Collections'
                },
                pagination : {
                    limit : 20,
                    total : 0,
                    total_page : 0,
                    current_page : 1,
                }
            }
        },
        computed: {
            ...mapGetters({
                collections : '$_Home/getCollections',
                countries : '$_Home/getCountries'
            })
        },
        methods: {
            getProduct(to = null){
                if( to == null){
                    to = this.$route
                }
                let sort = to.query.sort ? to.query.sort : null 
                sort = this.checkSort(sort) ? sort : this.default_sort
                /* get current_page */
                let page = to.query.page   ? to.query.page : 1
                if( page ) {
                    page = helper.isNumeric(page) ? page : 1
                }
                /* get collection_id */
                let collection_id = to.params.id   ? to.params.id : null
                if( collection_id ) {
                    collection_id = this.getSlugId(collection_id)
                }
                /* get supplier_id */
                let supplier_id = to.query.supplier_id  ? to.query.supplier_id : null 
                if( supplier_id ) {
                    supplier_id = this.checkSupplier(supplier_id) ? supplier_id : null
                }
                /* get shipping_from */
                let shipping_from = to.query.shipping_from ?  to.query.shipping_from : null
                if( shipping_from ){
                    shipping_from = this.checkCountry(shipping_from) ? shipping_from : null
                }
                /* get shipping_to */
                let shipping_to = to.query.shipping_to  ? to.query.shipping_to : null
                if( shipping_to ){
                    shipping_to = this.checkCountry(shipping_to) ? shipping_to : null
                }
                /* get prices */
                let prices = null
                let price = to.query.prices 
                if( price ){
                    var temp = price.split('-')
                    if( temp.length == 2){
                        var min = temp[0].trim()
                        var max = temp[1].trim()
                        if( helper.isNumeric(min) && helper.isNumeric(max) && parseInt(min) <= parseInt(max)){
                            prices = price
                        }
                    }
                }
                return new Promise((resolve , reject )=>{
                    this.$store.dispatch('$_Home/GET_LIST_PRODUCTS' , { 
                        sort : sort,
                        collection_id: collection_id,
                        page : page,
                        supplier_id: supplier_id,
                        shipping_from : shipping_from,
                        shipping_to : shipping_to,
                        prices : prices,
                        limit : this.pagination.limit,
                        is_premium : 1,
                    }).then(res=>{
                        let { status , data , pagination } = res
                        if( status ){
                            this.products = data
                            this.pagination.total = parseInt(pagination.total) 
                            this.pagination.total_page = parseInt(pagination.total_page ) 
                            this.pagination.current_page = parseInt(pagination.current_page) 
                        }
                    })
                    resolve()
                })
            },
            checkCountry(code){
                return  _.find(this.countries , { code : code}) != undefined
            },
            checkSupplier(id){
                return _.find(this.suppliers , { id : id}) != undefined
            },
            checkSort(sort){
                return this.sortables.indexOf(sort) >= 0 
            },
            getCollection(){
                var collection_id = this.getSlugId(to.params.id)
                var findIndex = _.findIndex(this.collections , { id : collection_id})
                if( findIndex >= 0 ){
                    var more = findIndex >= 10 ? true : false
                    this.$store.commit('$_Home/UPDATE_SIDEBAR_OPTION' , { collection_id : '' , more : more})
                }else{
                    for (let index = 0; index < this.collections.length; index++) {
                        const collection = this.collections[index]
                        var child = _.find( collection.childs , {id : collection_id})
                        if( child ){
                            var more = index >= 10 ? true : false
                            this.$store.commit('$_Home/UPDATE_SIDEBAR_OPTION' , { collection_id : child.parent_id , more : more})
                            break
                        }
                    }
                }
            }
        },
        beforeRouteUpdate : async function(to, from, next) {
            this.isLoading = true
            await this.getProduct(to)
            this.isLoading = false
            next()
        },
        created : async function(){
            this.isLoading = true
            var to = this.$route
            var collection_id = this.getSlugId(to.params.id)
            var findIndex = _.findIndex(this.collections , { id : collection_id})
            if( findIndex >= 0 ){
                var more = findIndex >= 10 ? true : false
                this.$store.commit('$_Home/UPDATE_SIDEBAR_OPTION' , { collection_id : '' , more : more})
            }else{
                for (let index = 0; index < this.collections.length; index++) {
                    const collection = this.collections[index]
                    var child = _.find( collection.childs , {id : collection_id})
                    if( child ){
                        var more = index >= 10 ? true : false
                        this.$store.commit('$_Home/UPDATE_SIDEBAR_OPTION' , { collection_id : child.parent_id , more : more})
                        break
                    }
                }
            }
            this.$store.commit('$_Home/UPDATE_SIDEBAR_OPTION' , { collection_id : '' , more : false})
            await this.getProduct(this.$route)
            this.isLoading = false
        }
    }
