import {mapActions, mapGetters} from 'vuex'
import ImageZoomer from '@/modules/home/_components/partials/vue-pic-zoom'
import {Carousel, Slide} from '@/components/v-carousel'
import ProductBodyHtml from '../partials/product-body-html'
import BestSellingProduct from '../partials/product-best-selling'
import ListProductFilter from '../partials/list-product-filter'
import Error from '@/components/error-404'
import helper from '@/plugins/helper'
import mixin from '@/modules/home/_mixins/product-detail'
import breadcrumb from '../partials/collection-breadcrumb'
import ProductQuestion from '../partials/product-question'
import ListFlag from '../partials/list-flag'

export default {
    name: 'ProductDetail',
    mixins: [mixin],
    components: {
        ImageZoomer,
        Carousel,
        Slide,
        ProductBodyHtml,
        BestSellingProduct,
        ListProductFilter,
        Error,
        breadcrumb,
        ProductQuestion,
        ListFlag
    },
    metaInfo() {
        return {
            title: this.meta.title,
            meta: [
                {
                    'name': 'og:type',
                    'vmid': 'og:type',
                    'property': 'og:type',
                    'content': 'product'
                },
                {
                    'name': 'og:url',
                    'vmid': 'og:url',
                    'property': 'og:url',
                    'content': this.meta.url
                },
                {
                    'name': 'og:title',
                    'vmid': 'og:title',
                    'property': 'og:title',
                    'content': this.meta.title
                },
                {
                    'name': 'og:description',
                    'vmid': 'og:description',
                    'property': 'og:description',
                    'content': this.meta.title
                },
                {
                    'name': 'og:image',
                    'vmid': 'og:image',
                    'property': 'og:image',
                    'content': this.meta.image
                },
            ]
        }
    },
    data() {
        return {
            isContentOverFlow: true,
            listBestSelling: [],
            data: null,
            isLoading: false,
            error: null,
            isImportLoading: false,
            meta: {
                title: '',
                url: '',
                image: '',
                description: '',
                type: ''
            }
        }
    },
    methods: {
        ...mapActions({
            getProductDetail: "$_Home/GET_PRODUCT_DETAIL",
            getListBestSelling: "$_Home/GET_LIST_BEST_SELLING",
            callImportProduct: "$_Home/IMPORT_PRODUCT"
        }),
        ...mapGetters({
            seller: '$_Seller/getSeller',
        }),
        importProduct(product, event) {
            if (this.seller()) {
                let id = product.id
                let target = $(event.target)
                this.isImportLoading = true
                let defaultShop = this.seller().shops.filter(function (el, idx) {
                    return el.shop_default == 1
                });
                let temp = {
                    "product_id": id,
                    "shop_id": defaultShop[0].id
                }
                this.callImportProduct(temp).then(res => {
                    if (res.data.status) {
                        this.ProductInfo.isImported = true
                    } else {
                        $notify({
                            content: 'import product fail',
                            type: 'danger'
                        })
                    }
                    this.isImportLoading = false
                })
            } else {
                this.$router.push({
                    name: "Login"
                })
            }
        },
        removeImported(product) {
            let id = product.id
            let target = $(event.target)
            this.isLoading = true
            let defaultShop = this.seller().shops.filter(function (el, idx) {
                return el.shop_default == 1
            });
            let temp = {
                "product_id": id,
                "shop_id": defaultShop[0].id
            }
            this.removeImportedProduct(temp).then(res => {
                if (res.data.status) {
                    this.ProductInfo.isImported = false
                } else {
                    $notify({
                        content: 'Remove Imported Product fail',
                        type: 'danger'
                    })
                }
                this.isLoading = false
            })
        },
        saveToRecent: function (id) {
            let ids = this.getLocalStore("lr")
            if (ids) {
                ids = ids.filter((item) => {
                    return item != id
                })
                if (ids.length >= 50) {
                    ids.shift()
                }
            } else {
                ids = []
            }
            ids.push(id)
            this.setLocalStore('lr', ids)
        },
        sticky_detail_bar() {
            var navbar = $(this.$refs.sticky)
            var body = $(this.$refs.body)
            if (navbar) {
                var stickyNavTop = body.outerHeight()
                var stickyNav = function () {
                    var scrollTop = $(window).scrollTop();
                    if (scrollTop > stickyNavTop) {
                        navbar.addClass('sticky');
                    } else {
                        navbar.removeClass('sticky');
                    }
                };
                stickyNav();
                $(window).scroll(function () {
                    stickyNav();
                });
            }
        },
        product_detail_init(id) {
            return new Promise(async (reslove, reject) => {
                this.isLoading = true
                var productValid = null
                var product = await this.getProductDetail({id})
                if (product) {
                    let {status, data, message} = product
                    if (status) {
                        if (data) {
                            this.saveToRecent(id)
                            this.data = data
                            productValid = true

                        } else {
                            this.error = `Product Not Available`
                        }
                    } else {
                        this.error = 'Something went wrong !'
                    }
                }
                this.isLoading = false
                this.$nextTick(() => {
                    setTimeout(() => {
                        this.sticky_detail_bar()
                    }, 600);
                })

                if (productValid) {
                    let listBestSelling = await this.getListBestSelling(id)
                    if (listBestSelling) {
                        let {status, data, message} = listBestSelling
                        if (status) {
                            this.listBestSelling = data
                        }
                    }
                }
                reslove()
            })
        },
        setMeta() {
            if (this.error) {

            } else {
                this.meta.title = this.data.title
                this.meta.url = `${this.data.handle}-${this.data.id}`
                this.meta.image = this.data.images.length ? this.data.images[0].src : ''
            }
        },
    },
    computed: {
        ...mapGetters({
            collections: '$_Home/getCollections',
            countries: '$_Home/getCountries',
            base_url: 'getBaseUrl'
        }),
        breadcrumb() {
            if (this.data) {
                var collection_id = this.data.collection_id
                var collection = helper.findDeep(this.collections, 'id', (item, id) => id == collection_id)
                if (collection) {
                    if (collection.level == 1) {
                        return [
                            {
                                id: collection.id,
                                title: collection.title,
                            }
                        ]
                    } else if (collection.level == 2) {
                        var title = []
                        var level1 = helper.findDeep(this.collections, 'id', (item, id) => id == collection.parent_id)
                        if (level1) {
                            title.push({
                                id: level1.id,
                                title: level1.title
                            })
                        }
                        title.push({
                            id: collection.id,
                            title: collection.title
                        })
                        return title
                    } else if (collection.level == 3) {
                        var title = []
                        var level2 = helper.findDeep(this.collections, 'id', (item, id) => id == collection.parent_id)
                        if (level2) {
                            var level1 = helper.findDeep(this.collections, 'id', (item, id) => id == level2.parent_id)
                            if (level1) {
                                title.push({
                                    id: level1.id,
                                    title: level1.title
                                })
                            }
                            title.push({
                                id: level2.id,
                                title: level2.title
                            })
                        }
                        title.push({
                            id: collection.id,
                            title: collection.title
                        })
                        return title
                    }

                }
            }
            return []
        },
        productShippings() {
            if (this.data) {
                var warehouses = JSON.parse(JSON.stringify(this.data.warehouses))
                warehouses = warehouses.map((item) => {
                    item['shippings'] = []
                    return item
                })
                for (var key in this.data.shippings) {
                    if (this.data.shippings.hasOwnProperty(key)) {
                        const item = this.data.shippings[key]
                        item.forEach((shipping) => {
                            var index = _.findIndex(warehouses, {id: shipping.warehouse_id})
                            if (index >= 0) {
                                if (shipping.rest_of_countries == 0) {
                                    var country_code = shipping.country_code;
                                    var split = country_code.split(',')
                                    shipping['countries'] = country_code.split(',').map((item) => {
                                        var code = _.find(this.countries, {code: item})
                                        return code
                                    }).filter(item => item != undefined)
                                }
                                warehouses[index].shippings.push(shipping)
                            }
                        })
                    }
                }
                return warehouses.filter((item) => {
                    return item.shippings.length
                })
            }
            return []
        }
    },
    beforeRouteUpdate: async function (to, from, next) {
        let id = this.getSlugId(to.fullPath);
        await this.product_detail_init(id)
        this.setMeta()
        next()
    },
    created: async function () {
        let to = this.$route
        let id = this.getSlugId(to.fullPath);
        await this.product_detail_init(id)
        this.setMeta()
    },
}
