@import 'src/assets/scss/static/variables';
.app-header-search{
    width: 100%;
    padding-top: 10px;
    .form-search{
        min-height: 50px;
        display: flex;
        flex-wrap: nowrap;
        flex-direction: row;
        &-input{
            flex: 100%;
            padding: 6px 15px;
            background: #fff;
            border-bottom-left-radius: 25px;
            border-top-left-radius: 25px;
            overflow: hidden;
            display: block;
            position: relative;
            .form-control{
                border-radius: 0;
                border: none;
                padding: 0 ;
                line-height: 36px;
                &:focus{
                    outline: none;
                    box-shadow: none;
                }
            }
            &:after{
                content: '';
                position: absolute;
                top: 20%;
                right: 0;
                width: 2px;
                bottom: 20%;
                background: $yellow;
            }
        }
        &-dropdown{
            white-space: nowrap;
            background: #fff;
            flex: auto;
            padding: 6px 0px;
            line-height: 36px;
            height: auto;
            text-overflow: ellipsis;
            .dropdown{
                &-toggle{
                    background: transparent;
                    border: none;
                    outline: none;
                    padding: 0 5px;
                    &:focus,&:hover{
                        background: transparent;
                        outline: none;
                        border: none;
                    }
                }
                &-menu{
                    max-width: 200px;
                    min-width: 100%;
                    white-space: nowrap;
                    text-overflow: ellipsis;
                    max-height: 45vh;
                    overflow: hidden auto;
                    a{
                        cursor: pointer;
                    }
                    ul{
                        margin: 0;
                        padding: 0;
                        li{
                            display: block;
                            a{
                                display: block;
                                padding: 4px 15px;
                                color:$text-color;
                                line-height: 1.5;
                                &:hover{
                                    background: $yellow;
                                    color:#fff;
                                }
                            }
                        }
                    }
                }
            }
        }
        &-action{
            flex: 1 1 75px;
            max-width: 75px;
            border-bottom-right-radius: 25px;
            border-top-right-radius: 25px;
            overflow: hidden;
            background: #fff;
            button{
                color:#fff;
                display: block;
                height: 100%;
                width: 100%;
                border-radius: 0;
                &:hover{
                    color:#fff;
                }
            }
        }
    }
}
